mapper.update:
UPDATE T_SB_BOARD
	SET BOARD_CNT = BOARD_CNT + 1
	WHERE BOARD_NO = #{boardNo}

UPDATE T_SB_BOARD
	SET BOARD_TITLE = #{boardTitle},
		BOARD_CONTENT = #{boardContent}
	WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT *
	FROM T_SB_BOARD
	WHERE BOARD_PW = #{boardPw}
	AND BOARD_NO = #{boardNo}
